import React from "react";
import icyimg from "../../../assets/icyrueter.png"
import { Grid, Button } from "@material-ui/core";

const Icy = () => {

    const style = {
        background: {
            backgroundColor: "black",
            color: "white",
            paddingTop: "20%",
            paddingBottom: "20%",
            height: "auto",
            width: "auto"
        },
        img: {
            height: 200,
            width: "auto"
        },
        button: {
            color: "yellow"
        }
    }
    return (
        <Grid container direction="row" alignItems="Center" justify="center" style={style.background}>
            <img style={style.img} src={icyimg} />
            <a href="https://www.amazon.com/Icy-Andrew-Rueter-ebook/dp/B07VDLZT9Y">
                <Button>Download from Amazon</Button>
            </a>
        </Grid>
    )
}

export default Icy;
