import React, { useState } from "react";
import './App.css';

import Slider from "./components/SliderComp/Slider";
import { Container, Grid, Typography, Paper, Tabs, Tab, Button } from "@material-ui/core";

import Poems from "./pages/Poems";
import Films from "./pages/Films";
import Contact from "./pages/Contact";


function App() {

  const [showPage, setShowPage ] = useState("AllPoems");

  const [activeButton, setActiveButton] = useState({
    poems: true,
    films: false,
    contact: false
  })

  const getPage = () => {
    switch(showPage) {
      case "AllFilms":
        return <Films />
      case "Contact":
        return <Contact />
      default:
        return <Poems />
    }
  }

  const setNav = (e, selection) => {
    e.preventDefault();
    switch(selection) {
      case "AllFilms":
        setActiveButton({ ...activeButton, films: true })
        setShowPage("AllFilms")
      case "Contact":
        setActiveButton({ ...activeButton, contact: true })
        setShowPage("Contact")
      default: 
        setActiveButton({ ...activeButton, poems: true })
        setShowPage("AllPoems")
    }
  }

  const style = {
    active: {
        color: "red"
    },
    inactive: {
        color: "black"
    }
  }



  return (
    <div>
      {/* Banner Section */}
      <Grid container direction="row" alignItems="center" justifyContent="center" className={"banner"}>
        <Grid item xs="12" md="6">
          <Typography align="center" variant="h3" centered className={"bannerFont"}>Andrew</Typography>
          <Typography align="center" variant="h3" centered className={"bannerFont"}>Rueter</Typography>
          <Typography align="center" variant="h3" centered className={"bannerFontPoetry"}>Poetry</Typography>
        </Grid>
        <Grid item xs="12" md="6" className={"bannerSlider"}>
          <Slider onChange={value => setShowPage(value)}/>
        </Grid>
      </Grid>
      {/* Nav Section */}
      <Paper>
        <Tabs centered>
        {
          (activeButton.poems)
          ? <Tab label="Poems" onClick={() => setShowPage("AllPoems")} style={style.active} />
          : <Tab label="Poems" onClick={() => setShowPage("AllPoems")} style={style.inactive} />
        }
        {
          (showPage == "AllFlims")
          ? <Tab label="Films" onClick={() => setShowPage("AllFilms")} style={style.active} />
          : <Tab label="Films" onClick={() => setShowPage("AllFilms")} style={style.inactive} />
        }
        {
          (showPage == "Contact")
          ? <Tab label="Contact" onClick={() => setShowPage("Contact")} className={"activeNav"} />
          : <Tab label="Contact" onClick={() => setShowPage("Contact")} className={"inactiveNav"} />
        }
        </Tabs>
      </Paper>
      <Container>
      {/* Body Section */}
        {getPage()}
      </Container>
    </div>
  );
}

export default App;
