import React, { useState } from "react";
import './App.css';

import Slider from "./components/SliderComp/Slider";
import { Container, Grid, Typography, Paper, Tabs, Tab } from "@material-ui/core";

import Poems from "./pages/Poems";
import Films from "./pages/Films";
import Contact from "./pages/Contact";


function App() {

  const [showPage, setShowPage ] = useState("AllPoems");

  const getPage = () => {
    switch(showPage) {
      case "AllFilms":
        return <Films />
      case "Contact":
        return <Contact />
      default:
        return <Poems />
    } 
  }

  const style = {
    activeNav: {
      color: "red"
    },
    inactiveNav: {
      color: "black"
    }
  }

  return (
    <div>
      {/* Banner Section */}
      <Grid container direction="row" alignItems="center" justifyContent="center" className={"banner"}>
        <Grid item xs="12" md="6">
          <Typography align="center" variant="h3" centered className={"bannerFont"}>Andrew</Typography>
          <Typography align="center" variant="h3" centered className={"bannerFont"}>Rueter</Typography>
          <Typography align="center" variant="h3" centered className={"bannerFontPoetry"}>Poetry</Typography>
        </Grid>
        <Grid item xs="12" md="6" className={"bannerSlider"}>
          <Slider onChange={value => setShowPage(value)}/>
        </Grid>
      </Grid>
      {/* Nav Section */}
      <Paper>
        <Tabs centered>
            <Tab label="Poems" onClick={() => setShowPage("AllPoems")} style={(showPage == "AllPoems" || showPage == "FeaturedPoems") ? style.activeNav : style.inactiveNav} />
            <Tab label="Films" onClick={() => setShowPage("AllFilms")} className={(showPage == "AllFlims" || showPage == "FeaturedFilms") ? "activeNav" : "inactiveNav"}/>
            <Tab label="Contact" onClick={() => setShowPage("Contact")} className={(showPage == "Contact") ? "activeNav" : "inactiveNav"}/>
        </Tabs>
      </Paper>
      <Container>
      {/* Body Section */}
        {getPage()}
      </Container>
    </div>
  );
}

export default App;
