import React, { useState } from 'react';
import { Grid, Card, CardActionArea, CardMedia, Typography, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Button, useMediaQuery, useTheme } from "@material-ui/core";

const FilmCard = (props) => {

    const style = {
        img: {
            height: 400,
            width: "auto"
        }
    }

    const [open, setOpen] = useState(false);
    const theme = useTheme();
    const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));

    const handleClickOpen = () => {
        setOpen(true);
      };

    const handleClose = () => {
    setOpen(false);
    };

    return (<>
        <Card onClick={handleClickOpen}>
            <CardActionArea>
                <CardMedia>
                    <img src={props.info.img} style={style.img}/>
                    <Typography variant="subtitle1"> {props.info.title} </Typography>
                </CardMedia>

            </CardActionArea>
        </Card>
        <Dialog
        fullScreen={fullScreen}
        open={open}
        onClose={handleClose}
        aria-labelledby="responsive-dialog-title"
      >
        <DialogTitle id="responsive-dialog-title">{props.info.title}</DialogTitle>
        <DialogContent>
          <DialogContentText>
          <Grid md="12">
                    <p><span>Relatablility - </span>{props.data.relatability}</p>
                    <p>Score: {props.data.relatabilityScore}</p>
                </Grid>
                <Grid md="12">
                    <p><span>Execution - </span>{props.data.execution}</p>
                    <p>Score: {props.data.executionScore}</p>
                </Grid>
                <Grid md="12">
                    <p><span>Context - </span>{props.data.context}</p>
                    <p>Score: {props.data.contextScore}</p>
                </Grid>
                <Grid md="12">
                    <p><span>Subtext - </span>{props.data.subtext}</p>
                    <p>Score: {props.data.subtextScore}</p>
                </Grid>
                <Grid md="12">
                    <p><span>Emotion - </span>{props.data.emotion}</p>
                    <p>Score: {props.data.emotionScore}</p>
                </Grid>
                <Grid item md="12">
                    <h5 style={styleMovieEntry.subheader}>Overall Rating: {props.data.overAllRating}</h5>
                </Grid>
                <Grid item md="12">
                    <h5><span style={styleMovieEntry.subheader}>Song: </span>{props.data.song}</h5>
                </Grid>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose} color="primary">
            Disagree
          </Button>
          <Button onClick={handleClose} color="primary" autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
      </>
    )
}

export default FilmCard;
