import React, { useState } from "react";
import { Grid, Button } from "@material-ui/core";

const FeaturedPoems = () => {

    const [selectPoems, setSelectPoems] = useState(false)
    const style = {
        background: {
            backgroundColor: "black",
            color: "white",
            paddingTop: "2%",
            paddingBottom: "2",
            height: "100%",
            width: "auto"
        }
    }

    const handleClick = (e) => {
        e.preventDefault();
        setSelectPoems(true);
    }
    return (
        <div style={style.background}>
            <Button onClick={e => handleClick(e)}></Button>
        </div>
    )
}

export default FeaturedPoems;
