import React, { useState } from 'react';
import { Container, Grid, Paper, Tabs, Tab } from "@material-ui/core";
import Poems from "./Poems";
import Films from "./Films";

const Home = () => {
    const [pageState, setPageState] = useState("Poems")

    switch(pageState) {
        case "Poems":
            return (
                <div>
                    <Paper>
                        <Tabs centered>
                            <Tab label="Poems" onClick={() => setPageState("Poems")}/>
                            <Tab label="Films" onClick={() => setPageState("Films")} />
                            <Tab label="Contact" />
                        </Tabs>
                    </Paper>
                    <Container>
                        <Grid container>
                            <Poems />
                        </Grid>
                    </Container>
                </div>
            )
        case "Films":
            return (
                <div>
                    <Paper>
                        <Tabs centered>
                            <Tab label="Poems" onClick={() => setPageState("Poems")} />
                            <Tab label="Films" onClick={() => setPageState("Films")}/>
                            <Tab label="Contact" />
                        </Tabs>
                    </Paper>
                    <Container>
                        <Grid container>
                            <Films />
                        </Grid>
                    </Container>
                </div>
            )
    }
}

export default Home;
