import React, { useState } from 'react';
import { Grid, Card, CardActionArea, CardMedia, Typography, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Button, useMediaQuery } from "@material-ui/core";

const FilmCard = (props) => {

    const style = {
        img: {
            height: 400,
            width: "auto"
        }
    }

    const [open, setOpen] = useState(false);
    const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));

    const handleClickOpen = () => {
        setOpen(true);
      };

    const handleClose = () => {
    setOpen(false);
    };

    return (<>
        <Card onClick={handleClickOpen}>
            <CardActionArea>
                <CardMedia>
                    <img src={props.info.img} style={style.img}/>
                    <Typography variant="subtitle1"> {props.info.title} </Typography>
                </CardMedia>

            </CardActionArea>
        </Card>
        <Dialog
        fullScreen={fullScreen}
        open={open}
        onClose={handleClose}
        aria-labelledby="responsive-dialog-title"
      >
        <DialogTitle id="responsive-dialog-title">{"Use Google's location service?"}</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Let Google help apps determine location. This means sending anonymous location data to
            Google, even when no apps are running.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose} color="primary">
            Disagree
          </Button>
          <Button onClick={handleClose} color="primary" autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
      </>
    )
}

export default FilmCard;
