import React, { useState, useEffect } from 'react';
import nl2br from "react-newline-to-break";
import PoemAPI from "../utilities/poems.json";
import { Grid, Typography } from "@material-ui/core";

import PoemCard from '../components/PoemCard';


const Poems = (props) => {

    const [filter, setFilter] = useState(props.page)

    const style= {
        column: {
            height: "auto",
            width: "auto"
        }
    }
    switch(filter){
    case "featuredpoems":
        return (
            <Grid
                container
                direction="row"
                justifyContent="center"
                alignItems="center"
                spacing={10}
                >
                    <Typography>Featured Poems</Typography>
                {PoemAPI.data.map(film => (
                <Grid item md={8} xs={12} style={style.column}><PoemCard info={film} text={nl2br(film.text)}/></Grid>
                    ))}
            </Grid>
        )
    default:
        return (
            <Grid
                container
                direction="row"
                justifyContent="center"
                alignItems="center"
                spacing={10}
                >
                {PoemAPI.data.map(film => (
                <Grid item md={8} xs={12} style={style.column}><PoemCard info={film} text={nl2br(film.text)}/></Grid>
                    ))}
            </Grid>
        )
    }
}

export default Poems;
