    };

    const style = {
        card: {
            height: "auto",
            width: "90%",
            border: "none",
            boxShadow: "none"
        },
        cardLong: {
            height: 200,
            width: "90%",
            border: "none",
            boxShadow: "none"
        },
        entry: {
